mapper.select:
select count(distinct t_article.id) from t_article left join t_article_tag on t_article.title = t_article_tag.title left join t_category on t_article.category_id=t_category.id left join t_tag on t_tag.name = t_article_tag.tag
1=1
and t_article.title like concat('%',#{condition.keywords},'%')
and t_article.category_id = #{condition.categoryId}
and t_tag.id = #{condition.tagId}

mapper.update:
update MyBlog.t_article
set title = #{article.title},category_id=#{article.category.id},content=#{article.content},isTop=#{article.isTop},
    updateTime=NOW() where id = #{article.id}

mapper.delete:
delete from t_article_tag where 1=1

    and title = #{article}


    and tag = #{tag}

mapper.update:
update t_article set view = case id
when #{value.id} then #{value.view}
end
